    public getThiesenStyle (level) {
        return new Style({
            fill: new Fill({
                color: level && level > 0 ? this.thiesenColor[level] : 'rgba(96,233,255,0.2)',
            }),
            stroke: new Stroke({
                color: '#2cdfff', // '#bdbdbd','#818181',//'#a0a0a0',
                width: 1
            })
        })
    }

    public getPointStyle (ele) {
        return new Style({
            image: new Circle({
                radius: ele.radius ? ele.radius : 6,
                fill: new Fill({
                    color: ele.color ? ele.color : "rgb(93, 198, 93)"
                }),
                stroke: new Stroke({
                    color: [255, 255, 255, 1],
                    width: 2
                })
            }),
            zIndex: Infinity
        });
    }

    public getTyphoonPointStyle (level, text: any) {
        if (text) {
            return new Style({
                image: new Circle({
                    radius: 4,
                    fill: new Fill({
                        color: this.typhoonPointColor[level ? level : 0]
                    }),
                    stroke: new Stroke({
                        color: [255, 255, 255, 1],
                        width: 0.5
                    })
                }),
                zIndex: Infinity,
                text: new Text({
                    // 字体与大小
                    font: text.font ? text.font : '16px Calibri sans-serif',
                    //文字填充色
                    fill: new Fill({
                        color: text.fillColor ? text.fillColor : '#000'
                    }),
                    //文字边界宽度与颜色
                    stroke: new Stroke({
                        color: text.strokeColor ? text.strokeColor : '#fff',
                        width: 1
                    }),
                    // 显示文本，数字需要转换为文本string类型！
                    text: text.text ? text.text : "",
                    offsetY: 15
                })
            });
        } else {
            return new Style({
                image: new Circle({
                    radius: 4,
                    fill: new Fill({
                        color: this.typhoonPointColor[level ? level : 0]
                    }),
                    stroke: new Stroke({
                        color: [255, 255, 255, 1],
                        width: 0.5
                    })
                }),
                zIndex: Infinity
            });
        }

    }

    public getTyphoonStyle (level) {
        return new Style({
            stroke: new Stroke({
                color: this.typhoonPointColor[level ? level : 0],
                width: 2
            })
        });
    }

    public getForecastStyle (type) {
        return new Style({
            fill: new Fill({
                color: this.forecastColor[type]
            }),
            stroke: new Stroke({
                //lineJoin:'bevel',
                lineDash: [1, 2, 3, 4, 5, 6],
                color: this.forecastColor[type],
                width: 2
            })
        })
    }
    public getWarningLineStyle (type) {
        const warningStyle: any = {
            color_24: "#FFFF00",
            color_48: "#160DFF",
            lineStyle_24: 5,
            lineStyle_48: 3,
        }

        return new Style({
            fill: new Fill({
                color: warningStyle["color_" + type]
            }),
            stroke: new Stroke({
                //lineJoin:'bevel',
                color: warningStyle["color_" + type],
                lineDash: [1, 3, 6],
                width: warningStyle["lineStyle_" + type]
            })
        })
    }

