    public getIconStyle (ele: any) {
        return new Style({
            image: new Icon({
                anchor: ele.anchor ? ele.anchor : [0.5, 0.96],
                crossOrigin: 'anonymous',
                src: ele.src
            })
        });
    }

    public getTextStyle (ele: any) {
        return new Text({
            // 字体与大小
            font: ele.font ? ele.font : '13px Microsoft YaHei',
            //文字填充色
            fill: Fill({
                color: ele.fillColor ? ele.fillColor : '#666'
            }),
            //文字边界宽度与颜色
            stroke: Stroke({
                color: ele.strokeColor ? ele.strokeColor : '#fff',
                width: 3
            }),
            // 显示文本，数字需要转换为文本string类型！
            text: ele.text ? ele.text : "",
            offsetY: -15
        })
    }

    public getMakerStyle (ele: any) {
        let makerStyle: any;
        switch (ele.type) {
            case "RAIN":
                makerStyle = this.getRainPointStyle(ele.value);
                break
            case "NLHD":
                makerStyle = this.getIconStyle({ src: this.MakerIcon[ele.value].source });
                break
            case "DZZH":
                console.log();
                makerStyle = this.getIconStyle({ src: this.MakerIcon[ele.value].source });
                break
            case "WEATHER":
                makerStyle = this.getIconStyle({ src: this.MakerIcon[ele.value].source });
                break
            case "WaterLog":
                makerStyle = new Style({
                    image: new Circle({
                        radius: 10,
                        fill: new Fill({
                            color: "#44f2ff"
                        }),
                        stroke: new Stroke({ color: [255, 255, 255, 1], width: 1 })
                    }),
                    zIndex: Infinity
                });
                break;
            default:
                makerStyle = new Style({
                    image: new Circle({
                        radius: 10,
                        fill: new Fill({
                            color: "#ff470d"
                        }),
                        stroke: new Stroke({
                            color: [255, 255, 255, 1],
                            width: 1
                        })
                    }),
                    zIndex: Infinity
                });
                break
        }
        return makerStyle;

    }

    private getRainPointStyle (opts: any) {
        let color: any = this.rainPointColor[0];
        if (opts >= 0 && opts <= 10) {
            color = this.rainPointColor[0];
        } else if (opts > 10 && opts <= 25) {
            color = this.rainPointColor[1];
        } else if (opts > 25 && opts <= 50) {
            color = this.rainPointColor[2];
        } else if (opts > 50 && opts <= 100) {
            color = this.rainPointColor[3];
        } else if (opts > 100 && opts <= 250) {
            color = this.rainPointColor[4];
        } else if (opts > 250) {
            color = this.rainPointColor[5];
        }
        return new Style({
            image: new Circle({
                radius: 10,
                fill: new Fill({
                    color: color
                }),
                stroke: new Stroke({
                    color: [255, 255, 255, 1],
                    width: 2
                })
            }),
            zIndex: Infinity
        });

    }

